module.exports = function( page, res ) {
   
   const
      OK       = 200,
      NOTFOUND = 404,
      HEADERS  = {
         'Content-Type': 'text/html'
      };
   
   let html = '                 \
      <!DOCTYPE html>           \
      <meta charset="utf-8">    \
      <title>Index page</title> \
      <p>Index page             \
   ';
   
   switch ( page ) {
      case '/':
      case '/index.html':
         res.writeHead( OK, HEADERS );
         res.end( html );
         console.log( `Page "${ page }" successfully served.` );
         break;
      default:
         res.statusCode = NOTFOUND;
         res.end();
         console.log( `Page "${ page }" not found, status "${ NOTFOUND }" served.` );
   };
   
};